//region internalFunctions
getLineLevel = function(lineObj) {
  switch (lineObj.type) {
    case "title":
      return 1;
    case "sectionHead":
      return 2;
    case "routeHead":
      return 3;
    case "requestHead":
    case "responseHead":
      return 4;
    default:
      return 7;
  }
};

getLineType = function(line) {
  var index;
  if (!contentDetect.test(line)) {
    return "emptyLine";
  }
  index = 0;
  while (line.charAt(index) === "#") {
    index++;
  }
  if (line.charAt(index) === " ") {
    switch (index) {
      case 1:
        return "title";
      case 2:
        return "sectionHead";
      case 3:
        return "routeHead";
      case 4:
        return requestOrResponse(line);
      default:
        return "contentLine";
    }
  }
  if (line.indexOf(jsonStart) === 0) {
    return "jsonStart";
  }
  if (line.indexOf(jsonEnd) === 0) {
    return "jsonEnd";
  }
  return "contentLine";
};

lineToBlockType = function(lineType) {
  return lineToBlockTypeMap[lineType];
};

requestOrResponse = function(line) {
  if (line.indexOf("request") > 4) {
    return "requestHead";
  } else if (line.indexOf("response") > 4) {
    return "responseHead";
  } else {
    return "contentLine";
  }
};

